import React, {useState, useEffect} from 'react'
import Cadastro from './Cadastro'
import axios from "axios";

export default function List() {
  const [cadastroList, setCadastroLit] =  useState[{}]

  useEffect(() => {
      refresCadastroList();
  }, [])


  const SeriesApi = (url = "https://localhost:44378/api/Cadastroes") => {
    return {
      fetchall: () => axios.get(url),
      create: newRecord => axios.post(url, newRecord),
      updated:(id, updatedRecord) => axios.put(url + id, updatedRecord),
      delete: id => axios.delete(url + id) 
    }
  }


  function refresCadastroList(){
    SeriesApi().fetchall()
    .then(res => setCadastroLit(res.data))
    .catch(err => console.log(err))
  }

  const addOrEdit = (formData, onSuccess) =>{
    SeriesApi().create(formData)
    .then(res =>{
      onSuccess();
      refresCadastroList();
    })
    .catch(err => console.log(err))
  }
  
  const imageCard = data =>(
    <div className="card">
      <img src={data.imageSrc} className="card-img-top rounded-circle" />
      <div className="card-body">
        <h5>{data.name}</h5>
        <span>{data.categoria}</span>
      </div>
    </div>
  )

  return (
     <div className="row">
       <div className='col-md-12'>
         <div class="jumbotron jumbotron-fluid py-4">
          <div class="container text-center">
         <h1 class="display-4">Fluid jumbotron</h1>
         </div>
      </div>
      </div>

        <div className="col-md-4">
         <Cadastro
          addOrEdit={addOrEdit}
          />
      </div> 

      <div className="col-md-8">
       <table>
         <tbody>
           {
             [...Array(Math.ceil(cadastroList.length/3))].map((e,i)=>
             <tr>
               <td>{cadastroList[3 * i] ? imageCard(cadastroList[3 * i]) : null}</td>
             </tr>
             )
           }
         </tbody>
       </table>
      </div>

   
    </div>
  )
}
