import React, {useState,useEffect} from 'react'

const defaultImagemSrc = "/img/Imge.jpg"

const initialFielValues ={
    Id: 0,
    Nome: '',
    Tema: '',
    ImagemUrl:'',
    ImagemSrc:defaultImagemSrc,
    ImagemFile:null

}


export default function Cadastro() {
    const [values,setValues]= useState(initialFielValues)
    
    const handleInputChange = e => {
        const {name, value} = e.target;
        setValues({
            ...values,
            [name]:values
        })
    }
    
    const showPreview = e =>{
        if(e.target.files && e.target.files[0]){
            let ImagemFile = e.target.files[0];
            const reader = new FileReader();
            reader.onload = x=>{
                setValues({
                    ...values,
                    ImagemFile,
                    ImagemSrc: x.target.result
                })
            }
            reader.readAsDataURL(ImagemFile)
        }else{
            setValues({
                ...values,
                ImagemFile:null,
                ImagemSrc: defaultImagemSrc
            })

        }
    }

  return (
       <>
          <div className="container text-center">
            <p className="lead">Lan√ßar n</p>
          </div>

        <form autoComplete="off" noValidate>
           <div className="card">
                <img src={values.ImagemSrc} className="card-img-top"/>
            <div className="card-body">
              <div className="form-group">
                  <input type="file" accept="imagem/*" className="form-control-file"
                  onChange={showPreview}/>
              </div>

               <div className="form-group">
                <div className="form-control" placeholder="Nome da Serie" name="Nome" 
                  value={values.Nome} onChange={ handleInputChange} />
               </div>
          
              <div className="form-group">
                 <div className="form-control" placeholder="Tema" name="Nome" 
                value={values.Tema}  onChange={ handleInputChange} />
               </div>
           
             <div className="form-group">
               <div className="form-control" placeholder="Upload" name="Nome" value={values.ImagemUrl}>
               </div>
             </div>

             <div className="form-group">
               <button type="button" class="btn btn-primary">Primary</button>
             </div>

            </div>
          </div>
        </form>
          
     
       </>
      
    )
}
