import React, {useState, useEffect} from 'react'
import Cadastro from './Cadastro'
import axios from "axios";

export default function List() {
  const [list, setList] =  useState([])
  
  useEffect(() => {
    refresList();
  }, [])
 
     

  const SeriesApi = (url = "https://localhost:44378/api/Cadastroes") => {
    return {
      fetchall: () => axios.get(url),
      create: newRecord => axios.post(url, newRecord),
      updated:(id, updatedRecord) => axios.put(url + id, updatedRecord),
      delete: id => axios.delete(url + id) 
    }
  }


  function refresList(){
    SeriesApi().fetchall()
    .then(res => setList(res.data))
    .catch(err => console.log(err))
  }

  const addOrEdit = (formData, onSuccess) =>{
    SeriesApi().create(formData)
    .then(res =>{
      onSuccess();
      refresList();
    })
    .catch(err => console.log(err))
  }

  
  
  const onDelete = (e, id) => {
    e.stopPropagation();
    if (window.confirm('Are you sure to delete this record?'))
        SeriesApi().delete(id)
            .then(res => refresList())
            .catch(err => console.log(err))
}


  const imageCard = data =>(
    <div className="card" onClick={() => { showRecordDetails(data) }}>
      <img src={data.imageSrc} className="card-img-top" />
      <div className="card-body">
        <h5>{data.name}</h5>
        <span>{data.categoria}</span>
        <button className="btn btn-light delete-button" onClick={e => onDelete(e, parseInt(data.employeeID))}>
                    <i className="far fa-trash-alt"></i>
                </button>
      </div>
    </div>
  )

  return (
     <div className="row">
       <div className='col-md-12'>
         <div class="jumbotron jumbotron-fluid py-4">
          <div class="container text-center">
         <h1 class="display-4" >Minhas Lista De Series</h1>
         </div>
      </div>
      </div>

         <div className="col-md-4">
         <Cadastro
          addOrEdit={addOrEdit}
          recordForEdit={recordForEdit}
          />
      </div>  

      <div className="col-md-8">
       <table>
         <tbody>
           {
             [...Array(Math.ceil(list.length / 3))].map((e, i) => 
               <tr key={i}>
                 <td>{imageCard(list[3 * i])}</td>
                 <td>{list[3 * i + 1] ? imageCard(list[3 * i + 1 ]) : null}</td>
                 <td>{list[3 * i + 2] ? imageCard(list[3 * i + 2 ]) : null}</td>
                
               </tr>
             )
           }
         </tbody>
       </table>
      </div>
    </div>
  )
}
