import React, {useState, useEffect} from 'react'
import Cadastro from './Cadastro'
import axios from "axios";



export default function List() {
  const [list, setEmployeeList] = useState([])
  const [recordForEdit, setRecordForEdit] = useState(null)

  useEffect(() => {
      refreshEmployeeList();
  }, [])

  const seriesAPI = (url = 'https://localhost:44378/api/Cadastroes/') => {
      return {
          fetchAll: () => axios.get(url),
          create: newRecord => axios.post(url, newRecord),
          update: (id, updatedRecord) => axios.put(url + id, updatedRecord),
          delete: id => axios.delete(url + id)
      }
  }

  function refreshEmployeeList() {
    seriesAPI().fetchAll()
         
          .catch(err => console.log(err))
  }

  const addOrEdit = (formData, onSuccess) => {
      seriesAPI().create(formData).then(res =>{
          onSuccess();
      })
      .catch(err => console.log(err))
  }
     
 
     
  

  const showRecordDetails = data => {
      setRecordForEdit(data)
  }

  const onDelete = (e, id) => {
      e.stopPropagation();
      if (window.confirm('Are you sure to delete this record?'))
          seriesAPI().delete(id)
              .then(res => refreshEmployeeList())
              .catch(err => console.log(err))
  }

  const imageCard = data => (
      <div className="card" onClick={() => { showRecordDetails(data) }}>
          <img src={data.imageSrc} className="card-img-top" />
          <div className="card-body">
              <h5>{data.name}</h5>
              <span>{data.categoria}</span> <br />
              <button className="btn btn-light delete-button" onClick={e => onDelete(e, parseInt(data.cadastroId))}>
                  <i className="far fa-trash-alt"></i>
              </button>
          </div>
      </div>
  )


  return (
      <div className="row">
          <div className="col-md-12">
              <div className="jumbotron jumbotron-fluid py-4">
                  <div className="container text-center">
                      <h1 className="display-4">Crud React Asp core net</h1>
                  </div>
              </div>
          </div>
          <div className="col-md-4">
              <Cadastro
                  addOrEdit={addOrEdit}
                 
              />
          </div>
          <div className="col-md-8">
              <table>
                  <tbody>
                      {
                          //tr > 3 td
                          [...Array(Math.ceil(list.length / 3))].map((e, i) =>
                              <tr key={i}>
                                  <td>{imageCard(list[3 * i])}</td>
                                  <td>{list[3 * i + 1] ? imageCard(list[3 * i + 1]) : null}</td>
                                  <td>{list[3 * i + 2] ? imageCard(list[3 * i + 2]) : null}</td>
                              </tr>
                          )
                      }
                  </tbody>
              </table>
          </div>
      </div>
  )
}
