import React, {useState,useEffect} from 'react'

const defaultImagemSrc = "/img/Imge.jpg"

const initialFielValues ={
    Id: 0,
    Nome: '',
    Tema: '',
    ImagemUrl:'',
    ImagemSrc:defaultImagemSrc,
    ImagemFile:null

}


export default function Cadastro(props) {

    const {addOrEdit} = props
    const [values,setValues]= useState(initialFielValues)
    const [erros, setErros]= useState({})
    
    const handleInputChange = e => {
        const {name, value} = e.target;
        setValues({
            ...values,
            [name]:values
        })
    }
    
    const showPreview = e =>{
        if(e.target.files && e.target.files[0]){
            let ImagemFile = e.target.files[0];
            const reader = new FileReader();
            reader.onload = x=>{
                setValues({
                    ...values,
                    ImagemFile,
                    ImagemSrc: x.target.result
                })
            }
            reader.readAsDataURL(ImagemFile)
        }else{
            setValues({
                ...values,
                ImagemFile:null,
                ImagemSrc: defaultImagemSrc
            })

        }
    }

    const validate = () => {
       let temp= {}
       temp.Nome = values.Nome==""?false:true;
       temp.ImagemSrc = values.ImagemSrc==defaultImagemSrc?false:true;
       temp.Tema = values.Tema==""?false:true;
       setErros(temp)
       return Object.values(temp).every(x => x==true)
    } 
     
    const resetForm = ()=>{
       setValues(initialFielValues)
       document.getElementById(image-uploader)
    }

    const handleFormSubmit = e => {
        e.preventDefault();
        if(validate()){
          const formData = new FormData()
           formData.append('Id', values.Id)
           formData.append('Nome', values.Nome)
           formData.append('Tema', values.Tema)
           formData.append('ImagemUrl', values.ImagemUrl)
           formData.append('ImagemFile', values.ImagemFile)
           addOrEdit(formData,resetForm)
        }
    }

    const applyErrorClass = field => ((field in erros && erros[field]==false)? 'invalid-field':'')

    

  return (
       <>
          <div className="container text-center">
            <p className="lead">Lan√ßar nova Series e Filmes</p>
          </div>

        <form autoComplete="off" noValidate onSubmit={handleFormSubmit}>
           <div className="card">
                <img src={values.ImagemSrc} className="card-img-top" />
            <div className="card-body">
              <div className="form-group">
                  <input type="file" accept="imagem/*" className={"form-control-file"+applyErrorClass('ImagemSrc')}
                  onChange={showPreview} id="image-uploader"/>
              </div>

               <div className="form-group">
                <input className={"form-control"+applyErrorClass('Nome')} placeholder="Nome da Serie" name="Nome" 
                  value={values.Nome} onChange={ handleInputChange} />
               </div>
          
              <div className="form-group">
                 <input className={"form-control"+applyErrorClass('Tema')} placeholder="Tema" name="Nome" 
                 value={values.Tema}  onChange={ handleInputChange} />
               </div>
           
          

             <div className="form-group">
               <button type="submit" class="btn btn-primary">Salvar</button>
             </div>

            </div>
          </div>
        </form>
          
     
       </>
      
    )
}
