import React, {useState,useEffect} from 'react'


const defaultImageSrc = '/img/Imge.jpg' 

const initialFieldValues ={
   cadastroId :0,
   name: '',
   categoria:'',
   imageName:'',
   imageSrc: defaultImageSrc, 
   imageFile: null
}

export default function Cadastro() {
 const [values, setValues] = useState(initialFieldValues)
 const [erros, setErros] = useState(initialFieldValues)

 const showPreview = e =>{
   if(e.target.files && e.target.files[0]){
     let imageFile = e.target.files[0];
     const reader = new FileReader();
     reader.onload = x=>{
       setValues({
         ...values,
         imageFile,
         imageSrc: x.target.result
       })
     }
     reader.readAsDataURL(imageFile)
   }
   else{
     setValues({
      ...values,
      imageFile:null,
      imageSrc: defaultImageSrc
    })
   }
 }


 const validate =()=>{
    let temp= {}
    temp.name = values.name==""?false:true;
    temp.imageSrc
 }

 const handleFormSubmit = e =>{
   e.preventDefault()
   if(validate()){

   }
 }

 const handleInputChange = e => {
   const {name, value} = e.target;
   setValues({
     ...values,
     [name]:value
   })
 }

  return (
       <>
          <div className="container text-center">
            <p className="lead">Lan√ßar nova Series e Filmes</p>
          </div>

        <form autoComplete="off" noValidate onSubmit={handleFormSubmit} >
           <div className="card">
              <img src={values.imageSrc} className="card-img-top"/> 
             <div className="card-body">
               <div className="form-group">
                  <input type="file" accept="image/*" className="form-control-file"
                  onChange={showPreview}/> 
               </div>
              <div className="form-group">
                <input className="form-control" placeholder="Name" nome="name"
                value={values.name} onChange = {handleInputChange}/>
              </div>
              <div className="form-group">
                <input className="form-control" placeholder="Categoria" nome="categoria"
                value={values.categoria} onChange = {handleInputChange}/>
              </div>
              <div className="form-group">
              <button type="submit" className="btn btn-primary">Salvar</button>
              </div>
             </div>
           </div>
        </form>
          
     
       </>
      
    )
}
